import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const ipoData = {
  upcoming: [
    {
      id: 1,
      company_name: "ABC Corp",
      price_band: "₹100 - ₹120",
      open_date: "2025-06-20",
      close_date: "2025-06-24",
    },
    {
      id: 2,
      company_name: "TechBuzz Ltd",
      price_band: "₹200 - ₹220",
      open_date: "2025-06-22",
      close_date: "2025-06-26",
    },
  ],
  ongoing: [
    {
      id: 3,
      company_name: "FinSoft Pvt Ltd",
      price_band: "₹150 - ₹170",
      open_date: "2025-06-15",
      close_date: "2025-06-19",
    },
  ],
  listed: [
    {
      id: 4,
      company_name: "MarketX",
      price_band: "₹250 - ₹270",
      open_date: "2025-05-01",
      close_date: "2025-05-05",
    },
  ],
};

const Home = () => {
  const [activeTab, setActiveTab] = useState('upcoming');

  return (
    <div className="container mt-4">
      <h2 className="mb-4 text-center">IPO Listings</h2>

      <ul className="nav nav-tabs justify-content-center">
        {['upcoming', 'ongoing', 'listed'].map((tab) => (
          <li className="nav-item" key={tab}>
            <button
              className={`nav-link ${activeTab === tab ? 'active' : ''}`}
              onClick={() => setActiveTab(tab)}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </button>
          </li>
        ))}
      </ul>

      <div className="mt-4">
        {ipoData[activeTab].map((ipo) => (
          <div className="card mb-3 shadow-sm" key={ipo.id}>
            <div className="card-body d-flex justify-content-between align-items-center">
              <div>
                <h5 className="card-title mb-1">{ipo.company_name}</h5>
                <p className="mb-1">Price Band: <strong>{ipo.price_band}</strong></p>
                <small>Open: {ipo.open_date} | Close: {ipo.close_date}</small>
              </div>
              <Link to={`/ipo/${ipo.id}`} className="btn btn-outline-primary">
                View Details
              </Link>
            </div>
          </div>
        ))}

        {ipoData[activeTab].length === 0 && (
          <p className="text-center text-muted mt-4">No IPOs in this category.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
